 import React, { useState, useRef, useEffect } from 'react';


function HotspotSection () {
  const [selectedIndex, setSelectedIndex] = useState(0);
  const sliderRef = useRef(null);
  const flickityRef = useRef(null);

  // Product data
  const products = [
    {
      id: 1,
      title: "The Cei Sueded Ribbon Top",
      price: "£304.00",
      color: "Taos Taupe",
      images: [
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/DEL-2_DEL-2__CEI-SUEDED-RIBBON-TOP_IGD3580_TAOS-TAUPE_2.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/DEL-2_DEL-2__CEI-SUEDED-RIBBON-TOP_IGD3580_TAOS-TAUPE_3.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/DEL-2_DEL-2__CEI-SUEDED-RIBBON-TOP_IGD3580_TAOS-TAUPE_6.jpg"
      ],
      variants: [
        { size: "XS", id: "xs1" },
        { size: "S", id: "s1" },
        { size: "M", id: "m1" },
        { size: "L", id: "l1" }
      ],
      hotspotPosition: { top: "34%", left: "45%", mobileTop: "39%", mobileLeft: "50%" }
    },
    {
      id: 2,
      title: "The Adel Pant",
      price: "£281.00",
      color: "Latte Melange",
      images: [
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/Del.-2-_Adel-Pant-_RWS3684_Latte-Melange_3.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/Del.-2-_Adel-Pant-_RWS3684_Latte-Melange_4.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/Del.-2-_Adel-Pant-_RWS3684_Latte-Melange_7.jpg"
      ],
      variants: [
        { size: "XS", id: "xs2" },
        { size: "S", id: "s2" },
        { size: "M", id: "m2" },
        { size: "L", id: "l2" }
      ],
      hotspotPosition: { top: "57%", left: "38%", mobileTop: "60%", mobileLeft: "55%" }
    },
    {
      id: 3,
      title: "The Marfa Suede Ankle Boot",
      price: "£381.00",
      color: "Antler",
      images: [
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/MarfaSuedeAnkleBoot_CHAU001_Antler_1.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/MarfaSuedeAnkleBoot_CHAU001_Antler_2.jpg",
        "https://pipeline-theme-fashion.myshopify.com/cdn/shop/products/MarfaSuedeAnkleBoot_CHAU001_Antler_3.jpg"
      ],
      variants: [
        { size: "6", id: "6" },
        { size: "7", id: "7" },
        { size: "8", id: "8" },
        { size: "9", id: "9" }
      ],
      hotspotPosition: { top: "70%", left: "14%", mobileTop: "65%", mobileLeft: "20%" }
    }
  ];

  // Initialize Flickity slider (you'll need to install flickity package)
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const Flickity = require('flickity');
      flickityRef.current = new Flickity(sliderRef.current, {
        cellAlign: 'left',
        contain: true,
        pageDots: true,
        prevNextButtons: true,
        selectedAttraction: 0.03,
        friction: 0.15,
        wrapAround: false
      });

      flickityRef.current.on('select', () => {
        setSelectedIndex(flickityRef.current.selectedIndex);
      });

      return () => {
        if (flickityRef.current) {
          flickityRef.current.destroy();
        }
      };
    }
  }, []);

  // Handle hotspot click
  const handleHotspotClick = (index) => {
    setSelectedIndex(index);
    if (flickityRef.current) {
      flickityRef.current.select(index);
    }
  };

  return (
    <div className="hotspot-section">
      <div className="section-header">
        <h3 className="section-kicker">Shop the look</h3>
      </div>

      <div className="hotspot-container">
        {/* Product Slider */}
        <div className="product-slider-container">
          <div className="product-slider" ref={sliderRef}>
            {products.map((product, index) => (
              <div key={product.id} className="product-slide">
                <ProductCard 
                  product={product} 
                  isSelected={selectedIndex === index}
                />
              </div>
            ))}
          </div>
        </div>

        {/* Hotspot Image */}
        <div className="hotspot-image-container">
          <div className="hotspot-image-wrapper">
            <img
              src="https://pipeline-theme-fashion.myshopify.com/cdn/shop/files/clothes-spr21-3.jpg"
              alt="Shop the look"
              className="hotspot-image"
            />
            
            {/* Hotspots */}
            {products.map((product, index) => (
              <button
                key={product.id}
                className={`hotspot-dot ${selectedIndex === index ? 'active' : ''}`}
                style={{
                  top: product.hotspotPosition.top,
                  left: product.hotspotPosition.left,
                  '--mobile-top': product.hotspotPosition.mobileTop,
                  '--mobile-left': product.hotspotPosition.mobileLeft
                }}
                onClick={() => handleHotspotClick(index)}
                aria-label={`View ${product.title}`}
              >
                <span className="hotspot-inner"></span>
                <span className="hotspot-pulse"></span>
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

const ProductCard = ({ product, isSelected }) => {
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  const handleNextImage = () => {
    setCurrentImageIndex((prev) => (prev + 1) % product.images.length);
  };

  return (
    <div className={`product-card ${isSelected ? 'selected' : ''}`}>
      <div className="product-image-container">
        <div className="product-image-wrapper">
          <img
            src={product.images[currentImageIndex]}
            alt={product.title}
            className="product-image"
          />
        </div>
        <div className="product-pagination">
          {product.images.map((_, index) => (
            <span 
              key={index} 
              className={`pagination-dot ${index === currentImageIndex ? 'active' : ''}`}
              onClick={() => setCurrentImageIndex(index)}
            ></span>
          ))}
        </div>
      </div>
      
      <div className="product-info">
        <h3 className="product-title">{product.title}</h3>
        <div className="product-price-container">
          <span className="product-color">{product.color}</span>
          <span className="product-price">{product.price}</span>
        </div>
        
        <div className="quick-add-toolbar">
          {product.variants.map(variant => (
            <button key={variant.id} className="quick-add-button">
              {variant.size}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

